import React from 'react'
import { propTypes } from 'utils'

export const Col = propTypes(
  ({ children, size }) => <div className={`col-xs-${size}`}>
    {children}
  </div>,
  {
    size: propTypes.integer.isRequired
  }
)

export const Row = ({ children }) => <div className='row'>
  {children}
</div>
