import React from 'react'
import {
  defineMessages,
  FormattedMessage
} from 'react-intl'

// ===================================================================

const messages = defineMessages({
  aboutPage: {
    id: 'aboutPage',
    defaultMessage: 'About'
  },
  homePage: {
    id: 'homePage',
    defaultMessage: 'Home'
  },

  usernameLabel: {
    id: 'usernameLabel',
    defaultMessage: 'Username'
  },
  passwordLabel: {
    id: 'passwordLabel',
    defaultMessage: 'Password'
  },
  signInButton: {
    id: 'signInButton',
    defaultMessage: 'Sign in'
  }
})

// ===================================================================

export default (messageId, values = {}) => <FormattedMessage
 {...messages[messageId]}
 values={values}
/>
